import React, { useState } from 'react'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import Button from '@material-ui/core/Button'
import Box from '@material-ui/core/Box'
import Autocomplete from '@material-ui/lab/Autocomplete'
import TextField from '@material-ui/core/TextField'
import data from '../assets/crypto_data'
import Container from '@material-ui/core/Container'
import Grid from '@material-ui/core/Grid'

const useStyles = makeStyles((theme) => ({
    logo: {
      marginLeft: theme.spacing(15)
    },
    form: {
      backgroundColor: 'white',
      padding: theme.spacing(5),
      borderRadius: 5,
      boxShadow: '0px 0px 10px 3px rgba(0, 0, 0, .3)',
      display: 'flex',
      flex: 1,
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }
  }))

  const BootstrapButton = withStyles({
    root: {
      display: 'inline-block',
      width: '100px',
      color: 'white',
      boxShadow: 'none',
      textTransform: 'none',
      fontSize: 14,
      padding: '6px 200px',
      border: '1px solid',
      lineHeight: 1.5,
      backgroundColor: '#1652F0',
      borderColor: '#0063cc',
      fontFamily: 'Red Hat Display',
      '&:hover': {
        backgroundColor: '#0069d9',
        borderColor: '#0062cc',
        boxShadow: 'none',
      },
      '&:active': {
        boxShadow: 'none',
        backgroundColor: '#0062cc',
        borderColor: '#005cbf',
      },
      '&:focus': {
        boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
      },
      '&:disabled': {
        backgroundColor: '#bababa',
        borderColor: '#bababa'
      }
    },
  })(Button);

const InputForm = ({setData, setIsLoading}) => {
    const classes = useStyles()
    const [selection, setSelection] = useState('')

    const selectCoin = (params) => {
        setSelection(params.inputProps.value)
        return <TextField {...params} fontFamily="Red Hat Display" label="Coin" variant="outlined" />
    }

    const getPrediction = () => {
      setIsLoading(true)
        fetch('https://trend-invest-web.herokuapp.com/predict?coinName=' + selection, {
        method: "GET",
        mode: "cors"
        }).then(res => res.json().then(data => {
            setData(data.data)
            setIsLoading(false)
        }).catch((error) => {
          console.error('Error:', error)
          setIsLoading(false)
      })
    )}

    return (
        <Grid container
         direction="row"
         justify="center"
         alignItems="center"
         className={classes.form}
        >
          <Grid item xs={12}>
            <Autocomplete
                id="combo-box-demo"
                options={data}
                getOptionLabel={(option) => option.title}
                style={{ width: 300 }}
                renderInput={(params) => selectCoin(params)}
            />
          </Grid>
          <Grid item xs={12}>
              <BootstrapButton 
               disabled={selection === ''} 
                onClick={() => getPrediction()}
                style={{ wdith: '100px' }}>
                    Submit
              </BootstrapButton>
          </Grid>
        </Grid>
    );
}

export default InputForm
